import logo from './logo.svg';
import './App.css';

import User from './User/User';
import { Component } from 'react';

class App extends Component {

  state = {
    users: [
      { name: 'Max', phone: '001', email: 'example@mail.com', country: 'USA', age: '23' },
      { name: 'Alex', phone: '002', email: 'example@mail.com', country: 'Russia', age: '30' },
      { name: 'Maria', phone: '003', email: 'example@mail.com', country: 'Australia', age: '34' },
      { name: 'Andy', phone: '004', email: 'example@mail.com', country: 'USA', age: '16' },
      { name: 'Helen', phone: '005', email: 'example@mail.com', country: 'Russia', age: '56' }
    ],
    pageTitle: 'React components'
  }

  changeTitleHandler = (newTitle) => {
    this.setState({
      pageTitle: newTitle
    })
  }

  render() {

    const users = this.state.users

    return (
      <div>
        <h1>{this.state.pageTitle}</h1>

        <button onClick={this.changeTitleHandler.bind(this, 'Changed!')}>Change Title</button>

        <User name={users[0].name}
        phone={users[0].phone}
        email={users[0].email}
        country={users[0].country}
        age={users[0].age} 
        onChangeTtile={this.changeTitleHandler}
        />
        <User name={users[1].name} phone={users[1].phone} email={users[1].email} country={users[1].country} age={users[1].age} />
        <User name={users[2].name} phone={users[2].phone} email={users[2].email} country={users[2].country} age={users[2].age} />
        <User name={users[3].name} phone={users[3].phone} email={users[3].email} country={users[3].country} age={users[3].age} />
        <User name={users[4].name} phone={users[4].phone} email={users[4].email} country={users[4].country} age={users[4].age} />


      </div>
    );
  }
}

export default App;
