import logo from './logo.svg';
import './App.css';

import User from './User/User';
import { Component } from 'react';

class App extends Component {

  state = {
    users: [
      { name: 'Max', phone: '001', email: 'example@mail.com', country: 'USA', age: '23' },
      { name: 'Alex', phone: '002', email: 'example@mail.com', country: 'Russia', age: '30' },
      { name: 'Maria', phone: '003', email: 'example@mail.com', country: 'Australia', age: '34' },
      { name: 'Andy', phone: '004', email: 'example@mail.com', country: 'USA', age: '16' },
      { name: 'Helen', phone: '005', email: 'example@mail.com', country: 'Russia', age: '56' }
    ],
    pageTitle: 'React components'
  }

  changeTitleHandler = (newTitle) => {
    this.setState({
      pageTitle: newTitle
    })
  }

  handleInput = (event) => {
    this.setState({
      pageTitle: event.target.value
    })
  }

  render() {

    return (
      <div>
        <h1>{this.state.pageTitle}</h1>
        {/* <input type="text" onChange={this.handleInput}></input>
        <button onClick={this.changeTitleHandler.bind()}>Change Title</button> */}

        {this.state.users.map((user, id) => {
          return (
            <User
              key={id}
              name={user.name}
              phone={user.phone}
              email={user.email}
              country={user.country}
              age={user.age}
              onChageTitle={() => this.changeTitleHandler(user.name)}
            />
          )
        })}

        {/* <User 
        name={users[1].name} 
        phone={users[1].phone}
        email={users[1].email}
        country={users[1].country}
        age={users[1].age} 
        onChangeTtile={()=> this.changeTitleHandler(users[1].name)}
        /> */}

        {/* <User name={users[0].name}
          phone={users[0].phone}
          email={users[0].email}
          country={users[0].country}
          age={users[0].age}
          onChangeTtile={this.changeTitleHandler.bind(this, users[0].name)}
        /> */}

      </div>
    );
  }
}

export default App;
